import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import Button from '@mui/material/Button';



import OrderInput from '../components/OrderInput';
import MenuItem from '../components/MenuItem';
import OrderItem from '../components/OrderItem'

function SubmitOrder({ orderInfo, removeOrderItem }) {

  const [totalPrice, setTotalPrice] = useState(0.00)

  useEffect(() => {
    let cost = 0.00
     orderInfo.items.map((item, index) => {
      setTotalPrice(totalPrice + item.itemPrice)
      console.log(totalPrice)
    })
  },[])

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }
  return (
    <div>
      <Grid container direction="column" justifyContent="center" alignItems="center">

        <Grid item xs={12}>
          <Typography variant='subtitle1'>{orderInfo.orderId}</Typography>
          <OrderInput inputType="Name" text={orderInfo.name}/>
          <OrderInput inputType="Phone" text={orderInfo.phone}/>
          <Grid container direction="row">
            <Button>Dine In</Button>
            <Button>To Go</Button>
            <Button>Delivery</Button>
          </Grid>


           {orderInfo.items.map((item, index) => (
              <OrderItem key={index} itemId={item.itemId} itemName={item.itemName} itemPrice={item.itemPrice} removeOrderItem={(itemId) => removeOrderItem(itemId)} />
           ))}
          <Typography>Price: {totalPrice}</Typography>

        </Grid>
        <Grid item xs={12}>
          <Button>Confirm Order</Button>

        </Grid>

      </Grid>
    </div>
  );
}

export default SubmitOrder;
