import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import SubmitOrder from './pages/SubmitOrder'
import Menu from './pages/Menu';
import MenuSection from './components/MenuSection';
import MenuItemData from './MenuItems.json'


function App() {
  const [displayPage, setDisplayPage] = useState("home")
  const [orderInfo, setOrderInfo] = useState({
    orderId: null,
    name: null,
    date: null,
    email: null,
    phone: null,
    items: []
  })



  const displaySubmitPage = () => {
    setDisplayPage("submit")
  }

  const updateOrderItem = (obj) => {
    let itemArray = []
    itemArray = orderInfo.items
    obj.itemId = orderInfo.items.length
    itemArray.push(obj)
    setOrderInfo({
      ...orderInfo,
      items: itemArray
    })
  }

  const removeOrderItem = (removeId) => {
    let itemArray = []
    itemArray = orderInfo.items

    const filteredOrderInfo = orderInfo.items.filter((item) => item.itemId !== removeId)

    setOrderInfo({
      ...orderInfo,
      items: filteredOrderInfo
    })
  }

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }

  return (
    <div>

      <Grid container direction="column" justifyContent="center" alignItems="center">
        <Grid item xs={12}>
           <Typography variant='h4'>Covington Fire Fighters</Typography>
        </Grid>
        <Grid item xs={12}>
           <Typography variant='h3'>Fish Fry</Typography>
        </Grid>
      </Grid>
      {(displayPage == "home") ? <Menu updateOrderItem={(obj) => updateOrderItem(obj)} displaySubmitPage={displaySubmitPage} orderInfoCount={orderInfo.items.length}/> : <SubmitOrder orderInfo={orderInfo} removeOrderItem={(itemId) => removeOrderItem(itemId)}/>}
    </div>
  );
}

export default App;
