import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import CircularProgress from '@mui/material/CircularProgress';
import Backdrop from '@mui/material/Backdrop';
import { useNavigate } from 'react-router-dom'


import OrderInput from '../components/OrderInput';
import MenuItemCard from '../components/MenuItemCard';
import OrderItem from '../components/OrderItem'

function SubmitOrder({ orderInfo, removeOrderItem, setTotalPrice, setName, setPhone, setOrderType, updateCompleteOrder }) {
  const navigate = useNavigate()

  const [isInProgress, setIsInProgress] = useState(false)

  useEffect(() => {
    let cost = 0.00
    orderInfo.items.map((item, index) => {
      cost = cost + item.itemPrice
    })
    setTotalPrice(cost)
  },[orderInfo.items])

  const delay = ms => new Promise(
    resolve => setTimeout(resolve, ms)
  );

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsInProgress(true)
    await delay(1000)
    fetch('http://covingtonfirefishserver-env.eba-mm2mz6gm.us-east-2.elasticbeanstalk.com/api/order/create', {
       method: 'POST',
       body: JSON.stringify({
        name: orderInfo.name,
        phone: orderInfo.phone,
        orderType: orderInfo.orderType,
        orderAddress: orderInfo.orderAddress,
        totalPrice: orderInfo.totalPrice,
        items: orderInfo.items
       }),
       headers: {
          'Content-type': 'application/json; charset=UTF-8',
       }
    })
    .then((res) => {
      res.json().then(body => updateCompleteOrder(body))
      setIsInProgress(false)
      navigate('/ordercomplete')
    })
    .catch((err) => {
      setIsInProgress(false)
      console.log(err.message)
    });
 };

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    },
    spinnerDisplay: {
      display: "flex"
    },
    spinnerHide: {
      display: "none"
    },
    selectedOrderType: {
      backgroundColor: "red"
    },
    notSelectedOrderType: {
      backgroundColor: "white"
    }
  }
  return (
    <div>       
      <Grid container direction="column" justifyContent="center" alignItems="center">
        <Grid item xs={12}>
          <TextField id="outlined-password-input" label="name" onChange={(e) => setName(e.target.value)} style={{ width: "100%" }}/>
          <TextField id="outlined-password-input" label="phone" onChange={(e) => setPhone(e.target.value)} style={{ width: "100%" }}/>
          <Grid container direction="row">
            <Button onClick={(orderType) => setOrderType("dine")} style={(orderInfo.orderType) == "dine" ? style.selectedOrderType : style.notSelectedOrderType}>Dine In</Button>
            <Button onClick={(orderType) => setOrderType("togo")} style={(orderInfo.orderType) == "togo" ? style.selectedOrderType : style.notSelectedOrderType}>To Go</Button>
            <Button onClick={(orderType) => setOrderType("delivery")} style={(orderInfo.orderType) == "delivery" ? style.selectedOrderType : style.notSelectedOrderType}>Delivery</Button>
          </Grid>

           {orderInfo.items.map((item, index) => (
              <OrderItem key={index} itemId={item.itemId} itemName={item.itemName} itemPrice={item.itemPrice} removeOrderItem={(itemId) => removeOrderItem(itemId)} />
           ))}
          <Typography>Price: {orderInfo.totalPrice}</Typography>
          <FormControl sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="demo-controlled-open-select-label">Age</InputLabel>
        <Select
          labelId="demo-controlled-open-select-label"
          id="demo-controlled-open-select"
          // open={open}
          // onClose={handleClose}
          // onOpen={handleOpen}
          // value={age}
          label="Age"
          // onChange={handleChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
        </Grid>
        <Grid item xs={12}>
          <Button onClick={handleSubmit} disabled={isInProgress}>Confirm Order</Button>
        </Grid>

      </Grid>
      <Backdrop
        sx={{ color: '#fff'}}
        open={isInProgress}
      >
        <CircularProgress />
      </Backdrop>
    </div>
  );
}

export default SubmitOrder;
