import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import CircularProgress from '@mui/material/CircularProgress';
import Backdrop from '@mui/material/Backdrop';


import OrderInput from '../components/OrderInput';
import MenuItem from '../components/MenuItem';
import CompletedOrderItem from '../components/CompletedOrderItem'

function OrderComplete({ orderInfo, resetOrder }) {

    const [completedOrder, setCompletedOrder] = useState({
        orderId: null,
        name: null,
        date: null,
        phone: null,
        items: [],
        totalPrice: 0.00,
        orderType: null,
        orderAddress: null
    })
    useEffect(() => {
        setCompletedOrder(orderInfo)
    }, [])

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    },
    spinnerDisplay: {
      display: "flex"
    },
    spinnerHide: {
      display: "none"
    }
  }
  return (
    <div>       
        <Grid container direction="column" justifyContent="center" alignItems="center">
            <Grid item xs={12}>
                <Typography>Order Complete</Typography>
                <Typography>OrderID: {orderInfo.orderId}</Typography>
                <Typography>Name: {orderInfo.name}</Typography>
                {orderInfo.items.map((item, index) => (
                  <OrderItem key={index} itemId={item.itemId} itemName={item.itemName} itemPrice={item.itemPrice} />
                ))}
            </Grid>
        </Grid>
    </div>
  );
}

export default OrderComplete;
