import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import SubmitOrder from './pages/SubmitOrder'

import MenuSection from './components/MenuSection';

import MenuItemData from './MenuItems.json'


function App() {
  const [displayPage, setDisplayPage] = useState("home")
  const [hasOrder, setHasOrder] = useState(false);
  const [orderInfo, setOrderInfo] = useState({
    orderId: null,
    name: null,
    date: null,
    email: null,
    phone: null,
    items: []
  })

  const displayHasOrder = () => {
    setHasOrder(true)
  }

  const updateOrderItem = (obj) => {
    let itemArray = []
    itemArray = orderInfo.items
    itemArray.push(obj)
    setOrderInfo({
      ...orderInfo,
      items: itemArray
    })
  }

  const removeOrderItem = (obj) => {
    let itemArray = []
    itemArray = orderInfo.items
    itemArray.push(obj)
    setOrderInfo({
      ...orderInfo,
      items: itemArray
    })
  }


  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }

  if (displayPage == "home")
  return (
    <div>
      <Grid container justifyContent="center">
        <Fab style={!hasOrder ? style.noOrder : style.hasOrder} onClick={() => setDisplayPage("submit")}>
          Submit Order
        </Fab>
      </Grid>


      <Grid container direction="column" justifyContent="center" alignItems="center">
        <Grid item xs={12}>
           <Typography variant='h4'>Covington Fire Fighters</Typography>
        </Grid>
        <Grid item xs={12}>
           <Typography variant='h3'>Fish Fry</Typography>
        </Grid>

      </Grid>

      {MenuItemData.map((menu, index) => (
        <MenuSection key={index} sectionName={menu.section} items={menu.items} displayHasOrder={displayHasOrder} updateOrderItem={(obj) => updateOrderItem(obj)}/>
      ))}
    </div>
  );

  return (
    <div>
      <SubmitOrder orderInfo={orderInfo} />
    </div>
  )
}

export default App;
