import { useState, useEffect } from 'react'
import Fab from '@mui/material/Fab';
import Grid from '@mui/material/Grid';
import MenuSection from '../components/MenuSection';
import MenuItemData from '../MenuItems.json'
import { Link } from 'react-router-dom'

function Menu({ updateOrderItem, displaySubmitPage, orderInfoCount, displayHasOrder, hasOrder }) {

  const style = {
    hasOrder: {
      display: "block",
      position: 'fixed', 

      borderRadius: 5,
      color: "white",
      backgroundColor: "black",
      width: "90%"



    },
    noOrder: {
      display: "none"
    },
    link: {
      display: 'block',
      bottom: 20, 
      position: 'fixed', 
      width: "90%",
      backgroundColor: "black",
      color: "white"
    }
  }

  return (

    <div>
      <Grid container justifyContent="center">
        {/* <Fab style={!hasOrder ? style.noOrder : style.hasOrder} onClick={displaySubmitPage}>
          <Link to="/submitorder">Submit Order - {orderInfoCount}</Link>

        </Fab> */}
        <Link to="/submitorder" style={style.link}>
          <Fab style={!hasOrder ? style.noOrder : style.hasOrder} onClick={displaySubmitPage}>
            Submit Order - {orderInfoCount}
          </Fab>
        </Link>
      </Grid>
      <div>
      {MenuItemData.map((menu, index) => (
        <MenuSection key={index} sectionName={menu.section} items={menu.items} displayHasOrder={displayHasOrder} updateOrderItem={(obj) => updateOrderItem(obj)} />
      ))}
      </div>
    </div>
  );
}

export default Menu;
