import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import CircularProgress from '@mui/material/CircularProgress';
import Backdrop from '@mui/material/Backdrop';


import OrderInput from '../components/OrderInput';
import MenuItem from '../components/MenuItem';
import OrderItem from '../components/OrderItem'

function OrderComplete({ orderInfo, removeOrderItem, setTotalPrice, setName, setPhone }) {

  const [isInProgress, setIsInProgress] = useState(false)

  useEffect(() => {
    let cost = 0.00
    orderInfo.items.map((item, index) => {
      cost = cost + item.itemPrice
    })
    setTotalPrice(cost)
  },[orderInfo.items])

  const delay = ms => new Promise(
    resolve => setTimeout(resolve, ms)
  );

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsInProgress(true)
    await delay(1000)
    fetch('http://covingtonfirefishserver-env.eba-mm2mz6gm.us-east-2.elasticbeanstalk.com/api/order/create', {
       method: 'POST',
       body: JSON.stringify({
        name: orderInfo.name,
        phone: orderInfo.phone,
        orderType: orderInfo.orderType,
        orderAddress: orderInfo.orderAddress,
        totalPrice: orderInfo.totalPrice,
        items: orderInfo.items
       }),
       headers: {
          'Content-type': 'application/json; charset=UTF-8',
       }
    })
    .then((res) => {
      res.json().then(body => console.log(body))
      setIsInProgress(false)
    })
    .catch((err) => {
      setIsInProgress(false)
      console.log(err.message)
    });
 };

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    },
    spinnerDisplay: {
      display: "flex"
    },
    spinnerHide: {
      display: "none"
    }
  }
  return (
    <div>       
        <Grid container direction="column" justifyContent="center" alignItems="center">
            <Grid item xs={12}>
                <Typography>Order Complete</Typography>
            </Grid>
        </Grid>
    </div>
  );
}

export default OrderComplete;
