import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';

import MenuSection from './components/MenuSection';

import MenuItemData from './MenuItems.json'
import useSubmitOrder from './hooks/useSubmitOrder';


function App() {
  const [hasOrder, setHasOrder] = useSubmitOrder(false);

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }
  return (
    <div>
      <Grid container justifyContent="center">
        <Fab style={!hasOrder ? style.noOrder : style.hasOrder}>
          Submit Order
        </Fab>
      </Grid>


      <Grid container direction="column" justifyContent="center" alignItems="center">
        <Grid item xs={12}>
           <Typography variant='h4'>Covington Fire Fighters</Typography>
        </Grid>
        <Grid item xs={12}>
           <Typography variant='h3'>Fish Fry</Typography>
        </Grid>

      </Grid>

      {MenuItemData.map(menu => (
        <MenuSection sectionName={menu.section} items={menu.items} />
      ))}



      {/* <MenuSection sectionName="Apps and Sides" />
      <MenuSection sectionName="Platters" />
      <MenuSection sectionName="Kids" />
      <MenuSection sectionName="Drinks" /> */}
    </div>
  );
}

export default App;
