import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';

import MenuSection from '../components/MenuSection';

import MenuItemData from '../MenuItems.json'


function Menu({ displayHasOrder, updateOrderItem }) {

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }

  return (
    <div>
      {MenuItemData.map((menu, index) => (
        <MenuSection key={index} sectionName={menu.section} items={menu.items} displayHasOrder={displayHasOrder} updateOrderItem={updateOrderItem}/>
      ))}
    </div>
  );
}

export default Menu;
