import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';




import OrderInput from '../components/OrderInput';
import MenuItem from '../components/MenuItem';
import OrderItem from '../components/OrderItem'

function SubmitOrder({ orderInfo, removeOrderItem, setTotalPrice, setName, setPhone }) {


  useEffect(() => {
    let cost = 0.00
    orderInfo.items.map((item, index) => {
      cost = cost + item.itemPrice
    })
    setTotalPrice(cost)
  },[orderInfo.items])


  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://covingtonfirefishserver-env.eba-mm2mz6gm.us-east-2.elasticbeanstalk.com/api/order/create', {
       method: 'POST',
       body: JSON.stringify({
        name: orderInfo.name,
        phone: orderInfo.phone,
        orderType: orderInfo.orderType,
        orderAddress: orderInfo.orderAddress,
        totalPrice: orderInfo.totalPrice,
        items: orderInfo.items
       }),
       headers: {
          'Content-type': 'application/json; charset=UTF-8',
       },
    })
    .then((res) => console.log(res.json()))
    .catch((err) => console.log(err.message));
 };

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }
  return (
    <div>
      <Grid container direction="column" justifyContent="center" alignItems="center">

        <Grid item xs={12}>
          {/* <Typography variant='subtitle1'>{orderInfo.orderId}</Typography> */}
          {/* <OrderInput inputType="Name" text={orderInfo.name}/> */}
          <TextField id="outlined-password-input" label="name" onChange={(e) => setName(e.target.value)} style={{ width: "100%" }}/>
          <TextField id="outlined-password-input" label="phone" onChange={(e) => setPhone(e.target.value)} style={{ width: "100%" }}/>

          {/* <OrderInput inputType="Phone" text={orderInfo.phone}/> */}
          <Grid container direction="row">
            <Button>Dine In</Button>
            <Button>To Go</Button>
            <Button>Delivery</Button>
          </Grid>
           {orderInfo.items.map((item, index) => (
              <OrderItem key={index} itemId={item.itemId} itemName={item.itemName} itemPrice={item.itemPrice} removeOrderItem={(itemId) => removeOrderItem(itemId)} />
           ))}
          <Typography>Price: {orderInfo.totalPrice}</Typography>
        </Grid>
        <Grid item xs={12}>
          <Button onClick={handleSubmit}>Confirm Order</Button>

        </Grid>

      </Grid>
    </div>
  );
}

export default SubmitOrder;
