import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';


function MenuItemCard({ itemName, itemPrice, displayHasOrder, updateOrderItem }) {
  const handleOrder = () => {
    displayHasOrder()
    updateOrderItem({ itemName: itemName, itemPrice: itemPrice })
  }
  return (
    <Card sx={{ minWidth: 275 }} style={{ border: "3px black solid", margin: "5% 1% 5% 1%" }}>
      <CardContent>
        <Grid container direction="row" alignItems="center">
            <Grid item xs={6}>
                <Typography sx={{ fontSize: 20 }} color="text.secondary" gutterBottom>
                    {itemName}
                </Typography>
            </Grid>
            <Grid item xs={3}>
                <Typography sx={{ fontSize: 20 }} color="text.secondary" gutterBottom>
                    ${itemPrice}
                </Typography>
            </Grid>
            <Grid item xs={3}>
              <CardActions>
                <Button style={{ backgroundColor: "red", color: "white", borderRadius: 30 }} onClick={handleOrder}>Order</Button>
              </CardActions>
            </Grid>
        </Grid>
      </CardContent>
    </Card>

  );
}

export default MenuItemCard;
