import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';

import MenuSection from './components/MenuSection';

import MenuItemData from './MenuItems.json'


function App() {
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
    console.log(value)
  };

  return (
    <Grid container justifyContent="center" style={{ border: "2px red solid"}}>

      <Box sx={{ maxWidth: { xs: 320, sm: 480 }, bgcolor: 'background.paper' }}>
      <h3>Covington Fire Fighters</h3>
      <h2>Fish Fry</h2>
        <Tabs
          value={value}
          onChange={handleChange}
          variant="scrollable"
          scrollButtons="auto"
          aria-label="scrollable auto tabs example"
          allowScrollButtonsMobile
        >
          <Tab label="Apps and Sides" value={0} />
          <Tab label="Platters" value={1}/>
          <Tab label="Kids" value={2}/>
          <Tab label="Drinks" value={3}/>
        </Tabs>
      </Box>
      {MenuItemData.map(menu => (
        <MenuSection sectionName={menu.section} items={menu.items} />
      ))}
      {/* <MenuSection sectionName="Apps and Sides" />
      <MenuSection sectionName="Platters" />
      <MenuSection sectionName="Kids" />
      <MenuSection sectionName="Drinks" /> */}
    </Grid>
  );
}

export default App;
