import { useState, useEffect } from 'react'
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Fab from '@mui/material/Fab';


import OrderInput from '../components/OrderInput';


function SubmitOrder({ orderInfo }) {

  const style = {
    hasOrder: {
      bottom: 20, 
      position: 'fixed', 
      width: "90%", 
      borderRadius: 5, 
      backgroundColor: "black",
      color: "white",
      display: "block"
    },
    noOrder: {
      display: "none"
    }
  }
  return (
    <div>


      <Grid container direction="column" justifyContent="center" alignItems="center">
        <Grid item xs={12}>
           <Typography variant='h4'>Covington Fire Fighters</Typography>
        </Grid>
        <Grid item xs={12}>
           <Typography variant='h3'>Fish Fry</Typography>
        </Grid>

      </Grid>

      <Grid container direction="column" justifyContent="center" alignItems="center">

        <Grid item xs={12}>
          {(orderInfo.name != null ? <Typography variant='subtitle1'>{orderInfo.orderId}</Typography> : <OrderInput inputType="Name" />)}
           <Typography variant='subtitle1'>{orderInfo.orderId}</Typography>
           <Typography variant='subtitle1'>{orderInfo.name}</Typography>

           <Typography variant='subtitle1'>{orderInfo.phone}</Typography>

           <Typography variant='subtitle1'>{orderInfo.email}</Typography>

           {orderInfo.items.map(item => (
              <div>
                <Typography variant='subtitle1'>{item.itemName}</Typography>
                <Typography variant='subtitle1'>{item.itemPrice}</Typography>
              </div>
           ))}

        </Grid>

      </Grid>
    </div>
  );
}

export default SubmitOrder;
