import { useState, useEffect } from 'react'
import Fab from '@mui/material/Fab';
import Grid from '@mui/material/Grid';
import MenuSection from '../components/MenuSection';
import MenuItemData from '../MenuItems.json'
import { Link, useNavigate } from 'react-router-dom'

function Menu({ updateOrderItem, displaySubmitPage, orderInfoCount, displayHasOrder, hasOrder }) {
  const navigate = useNavigate()

  const navigateToSubmitOrder = () => {
    navigate('/submitorder')
  }
  const style = {
    hasOrder: {

      bottom: 20, 
      display: "block",
      position: 'fixed',
      borderRadius: 5,
      color: 'white',
      backgroundColor: "black",
      width: "90%",
      height: 50,
      fontSize: 20,
      marginLeft: 20,
      marginRight: 20,
      marginTop: 30,
      marginBottom: 30
    },
    noOrder: {
      display: "none"
    },
    link: {
      width: "100%",
      backgroundColor: "black",
      color: "white"
    }
  }

  return (
    <div style={{ marginBottom: 120 }}>
      {/* <Link to="/submitorder" > */}
          <Fab style={!hasOrder ? style.noOrder : style.hasOrder} onClick={navigateToSubmitOrder}>
            Submit Order - {orderInfoCount}
          </Fab>
        {/* </Link> */}
      <div>
        {MenuItemData.map((menu, index) => (
          <MenuSection key={index} sectionName={menu.section} items={menu.items} displayHasOrder={displayHasOrder} updateOrderItem={(obj) => updateOrderItem(obj)} />
        ))}
      </div>
    </div>
  );
}

export default Menu;
